import React from "react";
import "./BookInfo.css";

function BookInfo({ title, author, genre, year, pages, description, rating }) {
  return (
    <div className="book-info">
      <h1>{title}</h1>
      <h2>{author}</h2>
      
      <div className="book-meta">
        <span>{genre}</span>
        <span>{year} год</span>
        <span>{pages} страниц</span>
        <span>★ {rating}</span>
      </div>
      
      <div className="book-description">
        <h3>Описание</h3>
        <p>{description}</p>
      </div>
    </div>
  );
}

export default BookInfo;
