import React from "react";
import "./BookInfo.css";

function BookInfo({ title, author, year, publish, isbn, genre, description, keywords }) {
  return (
    <div className="book-info">
      <h1>{title}</h1>
      <h2>{author} ({year})</h2>
      
      <div className="book-meta">
        <span>Издательство: {publish}</span>
        <span>ISBN: {isbn}</span>
        <span>Жанр: {genre}</span>
      </div>
      
      <div className="book-description">
        <h3>Описание</h3>
        <p>{description}</p>
      </div>

      {keywords && (
        <div className="book-keywords">
          <h4>Ключевые слова:</h4>
          <p>{keywords}</p>
        </div>
      )}
    </div>
  );
}

export default BookInfo;
