import axios from 'axios';

const api = axios.create({
  baseURL: 'https://d527-178-208-64-74.ngrok-free.app/books',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Автоматически добавляем путь к изображениям
api.interceptors.response.use(response => {
  if (Array.isArray(response.data)) {
    response.data = response.data.map(item => ({
      ...item,
      coverImage: `/images/book-covers/${item.coverImage}`
    }));
  } else if (response.data?.coverImage) {
    response.data.coverImage = `/images/book-covers/${response.data.coverImage}`;
  }
  return response;
});

export default api;
