import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { mockBooks } from '../../data/mockData'; // Импортируем из mockData.js
import { api } from '../../api'; // Импортируем наш api из отдельного файла
import './AddBookPage.css';

const AddBookPage = () => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    isbn: '',
    publicationYear: '',
    publisher: '',
    genre: '',
    description: '',
    keywords: '',
    coverUrl: ''
  });
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      // Получаем текущие книги из localStorage или используем моковые данные по умолчанию
      const currentBooks = JSON.parse(localStorage.getItem('mockBooks')) || mockBooks;
      
      const newBook = {
        bookId: Math.max(...currentBooks.map(b => b.bookId), 0) + 1,
        ...formData,
        publicationYear: parseInt(formData.publicationYear)
      };
      
      localStorage.setItem('mockBooks', JSON.stringify([...currentBooks, newBook]));
      
      await new Promise(resolve => setTimeout(resolve, 800));
      navigate('/');
    } catch (err) {
      console.error('Ошибка:', err);
      setError('Не удалось добавить книгу');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-book-page">
      <h2>Добавить новую книгу</h2>
      {isUsingMockData && (
        <div className="warning-banner">
          Сервер недоступен. Книга сохранена локально.
        </div>
      )}
      {error && <div className="error-message">{error}</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Название:</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Автор:</label>
          <input
            type="text"
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>ISBN:</label>
          <input
            type="text"
            name="isbn"
            value={formData.isbn}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Год издания:</label>
          <input
            type="number"
            name="publicationYear"
            value={formData.publicationYear}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Издательство:</label>
          <input
            type="text"
            name="publisher"
            value={formData.publisher}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Жанр:</label>
          <input
            type="text"
            name="genre"
            value={formData.genre}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Описание:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Ключевые слова (через запятую):</label>
          <input
            type="text"
            name="keywords"
            value={formData.keywords}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Ссылка на обложку:</label>
          <input
            type="url"
            name="coverUrl"
            value={formData.coverUrl}
            onChange={handleChange}
            required
          />
          {formData.coverUrl && (
            <div className="cover-preview">
              <img src={formData.coverUrl} alt="Превью обложки" />
            </div>
          )}
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Добавление...' : 'Добавить книгу'}
        </button>
      </form>
    </div>
  );
};



export default AddBookPage;
