function BookCard({ book }) {
    return (
      <Link to={`/book/${book.id}`} className="book-card">
        <img 
          src={book.coverImage} 
          alt={`Обложка: ${book.title}`}
          onError={(e) => {
            e.target.src = '/images/default-book-cover.png'; // Фолбек
          }}
        />
        <div className="book-card__info">
          <h3>{book.title}</h3>
          <p>{book.author} ({book.year})</p>
        </div>
      </Link>
    );
  }
