import React, { useState, useEffect } from "react";
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import "./BookPage.css";
import axios from 'axios';

function BookPage() {
  const [roleName, setRoleName] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Конфигурация защищённого запроса
  const api = axios.create({
    baseURL: 'https://d527-178-208-64-74.ngrok-free.app/books',
    headers: {
      'ngrok-skip-browser-warning': 'true' // Пропуск предупреждения ngrok
    },
    timeout: 5000
  });

  useEffect(() => {
    const fetchRoleName = async () => {
      try {
        const response = await api.get('/books/1');
        setRoleName(response.data.cover_url);
      } catch (err) {
        console.error("Ошибка запроса:", err);
        setError(err.response?.status === 401 
          ? "Ошибка авторизации" 
          : "Ошибка сервера");
        setRoleName("Демо-режим");
      } finally {
        setLoading(false);
      }
    };

    fetchRoleName();
  }, []);

  // Данные книги (моковые)
  const bookData = {
    title: "Обломов" + (roleName ? ` (${roleName})` : ""),
    author: "Иван Гончаров",
    coverImage: cover_url,
    year: 2023,
    publish: "Издательство 'Эксмо'",
    isbn: "978-5-04-193621-1",
    pages: 544,
    description: "Описание книги..."
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="book-page">
      <div className="book-main">
        <div className="book-but">
          <BookCover image={bookData.coverImage} />
          <ActionButtons />
        </div>
        <div className="book-details">
          <BookInfo {...bookData} />
        </div>
      </div>
    </div>
  );
}

export default BookPage;
