import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from "./components/Header";
import Footer from "./components/Footer";
import CatalogPage from "./pages/CatalogPage/CatalogPage"; // Новый компонент
import BookPage from "./pages/BookPage/BookPage";
import NotFoundPage from "./pages/NotFoundPage/NotFoundPage"; // Для 404 ошибок
import "./App.css"; // Глобальные стили

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <main>
          <Routes>
            {/* Главная страница с каталогом */}
            <Route path="/" element={<CatalogPage />} />

            {/* Страница конкретной книги */}
            <Route path="/book/:id" element={<BookPage />} />

            {/* Страница 404 (если маршрут не найден) */}
            <Route path="*" element={<NotFoundPage />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
