import React, { useState, useEffect } from "react";
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import "./BookPage.css";
import axios from 'axios';

function BookPage() {
  const [roleName, setRoleName] = useState(""); // Состояние для role_name
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Данные книги (остаются моковыми, так как вы получаете только роль)
  const bookData = {
    title: "Обломов",
    author: "Иван Гончаров",
    coverImage: "/images/book.png",
    year: 2023,
    publish: "Издательство 'Эксмо'",
    isbn: "978-5-04-193621-1",
    pages: 544,
    description: "Иван Александрович Гончаров (1812—1891) писал свой лучший роман..."
  };

  // Загружаем role_name с бэкенда
  useEffect(() => {
    const fetchRoleName = async () => {
      try {
        const response = await axios.get('https://0e1e-178-208-64-74.ngrok-free.app/roles');
        setRoleName(response.data.role_name || "Роль не определена");
      } catch (err) {
        console.error("Ошибка при загрузке роли:", err);
        setError("Не удалось загрузить данные роли");
        setRoleName("Роль не загружена"); // Резервное значение
      } finally {
        setLoading(false);
      }
    };

    fetchRoleName();
  }, []);

  if (loading) {
    return <div className="loading">Загрузка данных роли...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="book-page">
      {/* Блок с информацией о роли */}
      <div className="role-info">
        <h3>Текущая роль:</h3>
        <p>{roleName}</p>
      </div>

      {/* Остальная часть страницы книги */}
      <div className="book-main">
        <div className="book-but">
          <BookCover image={bookData.coverImage} />
          <ActionButtons />
        </div>
        <div className="book-details">
          <BookInfo 
            title={bookData.title}
            author={bookData.author}
            year={bookData.year}
            publish={bookData.publish}
            isbn={bookData.isbn}
            pages={bookData.pages}
            description={bookData.description}
          />
        </div>
      </div>
    </div>
  );
}

export default BookPage;
