import React, { useState, useEffect } from "react";
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import "./BookPage.css";
import axios from 'axios';

function BookPage() {
  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Конфигурация API
  const api = axios.create({
    baseURL: 'https://d527-178-208-64-74.ngrok-free.app',
    headers: {
      'ngrok-skip-browser-warning': 'true'
    },
    timeout: 5000
  });

  useEffect(() => {
    const fetchBookData = async () => {
      try {
        const response = await api.get('/books/1');
        const apiData = response.data;
        
        setBookData({
          title: apiData.title,
          author: apiData.author,
          coverImage: apiData.coverUrl, // Используем coverUrl из API
          year: apiData.publicationYear,
          publish: apiData.publisher,
          isbn: apiData.isbn,
          pages: 320, // Можно добавить в API или оставить по умолчанию
          genre: apiData.genre,
          description: apiData.description,
          keywords: apiData.keywords
        });
      } catch (err) {
        console.error("Ошибка запроса:", err);
        setError(err.response?.status === 401 
          ? "Ошибка авторизации" 
          : "Ошибка сервера");
        
        // Демо-данные на случай ошибки
        setBookData({
          title: "19uujhjhgjg84 (Демо-режим)",
          author: "Джордж Оруэлл",
          coverImage: "https://i.imgur.com/oF7gvG4.jpeg",
          year: 1949,
          publish: "Издательство АСТ",
          isbn: "978-0-452-28423-4",
          pages: 320,
          genre: "Антиутопия",
          description: "Роман-антиутопия о тоталитарном государстве...",
          keywords: "антиутопия, тоталитаризм, свобода"
        });
      } finally {
        setLoading(false);
      }
    };

    fetchBookData();
  }, []);

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!bookData) return <div className="error">Данные о книге не загружены</div>;

  return (
    <div className="book-page">
      <div className="book-main">
        <div className="book-but">
          <BookCover image={bookData.coverImage} />
          <ActionButtons />
        </div>
        <div className="book-details">
          <BookInfo {...bookData} />
        </div>
      </div>
    </div>
  );
}

export default BookPage;
