import React, { useState, useEffect } from "react";
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import "./BookPage.css";
import axios from 'axios';

function BookPage() {
  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Конфигурация защищённого запроса
  const api = axios.create({
    baseURL: 'https://d527-178-208-64-74.ngrok-free.app',
    headers: {
      'ngrok-skip-browser-warning': 'true' // Пропуск предупреждения ngrok
    },
    timeout: 5000
  });

  useEffect(() => {
    const fetchBookData = async () => {
      try {
        const response = await api.get('/books/1');
        setBookData({
          title: `Обломов${response.data.role_name ? ` (${response.data.role_name})` : ""}`,
          author: "Иван Гончаров",
          coverImage: response.data.cover_url, // Используем cover_url из API
          year: 2023,
          publish: "Издательство 'Эксмо'",
          isbn: "978-5-04-193621-1",
          pages: 544,
          description: "Описание книги..."
        });
      } catch (err) {
        console.error("Ошибка запроса:", err);
        setError(err.response?.status === 401 
          ? "Ошибка авторизации" 
          : "Ошибка сервера");
        
        // Устанавливаем демо-данные при ошибке
        setBookData({
          title: "Обломов (Демо-режим)",
          author: "Иван Гончаров",
          coverImage: "https://via.placeholder.com/300x400", // Заглушка для обложки
          year: 2023,
          publish: "Издательство 'Эксмо'",
          isbn: "978-5-04-193621-1",
          pages: 544,
          description: "Описание книги..."
        });
      } finally {
        setLoading(false);
      }
    };

    fetchBookData();
  }, []);

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!bookData) return <div className="error">Данные о книге не загружены</div>;

  return (
    <div className="book-page">
      <div className="book-main">
        <div className="book-but">
          <BookCover image={bookData.coverImage} />
          <ActionButtons />
        </div>
        <div className="book-details">
          <BookInfo {...bookData} />
        </div>
      </div>
    </div>
  );
}

export default BookPage;
