import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import api from '../../api'; // Ваш настроенный axios-клиент
import './BookPage.css';

function BookPage() {
  const { id } = useParams(); // Получаем ID книги из URL
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Загружаем данные книги
  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await api.get(`/roles/`);
        setBook(response.data);
      } catch (err) {
        setError(err.response?.data?.message || err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]);

  // Статусы загрузки
  if (loading) {
    return (
      <div className="book-page">
        <div className="loading-spinner">Загрузка...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="book-page">
        <div className="error-message">
          Ошибка: {error}
          <button onClick={() => window.location.reload()}>Повторить</button>
        </div>
      </div>
    );
  }

  if (!book) {
    return (
      <div className="book-page">
        <div className="not-found">Книга не найдена</div>
      </div>
    );
  }

  // Основной рендер
  return (
    <div className="book-page">
      <div className="book-main">
        <div className="book-cover-section">
          <BookCover image={book.coverImage} />
          <ActionButtons 
            bookId={book.id} 
            isFavorite={book.isFavorite} // Пример доп. пропсов
          />
        </div>
        
        <div className="book-details">
          <BookInfo 
            title={book.title}
            author={book.author}
            year={book.year}
            publish={book.publisher || 'Издательство не указано'} // fallback
            isbn={book.isbn}
            pages={book.pages}
            description={book.description}
            rating={book.rating} // Доп. поле, если есть
          />
        </div>
      </div>
    </div>
  );
}

export default BookPage;
