import React from "react";
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import "./BookPage.css";
import api from '../../api'; // Ваш axios-клиент

function BookPage() {
  // Пример данных книги (в реальности будут приходить из API)
  
  
  const bookData = {
    title: "Обломов",
    author: "Иван Гончаров",
    coverImage: "/images/book.png",
    year: 2023,
    publish: "Издательство 'Эксмо'",
    isbn: "978-5-04-193621-1",
    pages: 544,
    description: "Иван Александрович Гончаров (1812—1891) писал свой лучший роман «Обломов» с длительным перерывом из-за морского путешествия, сначала опубликовал одну главу, которую впоследствии сам раскритиковал и переделал, а потом еще год редактировал текст. Такую же размеренность и неторопливость автор заложил и в образ главного героя. Добрый, умный и поэтичный помещик Илья Ильич Обломов мог бы иметь все, что пожелает, однако проводит свои дни в ленном ничегонеделании на любимом диване, предаваясь воспоминаниям и грезам. Для преодоления своего затворничества у него нет ни сил, ни желания. И даже любовь не может победить его апатию. Гончаров раскрывает перед нами дилемму человека, который упускает все возможности, но находит счастье и покой в своем положении, и одновременно выдает собирательный образ общественного застоя середины XIX века. Однако «обломовщина» прочно вошла в наш культурный код и оказалась вневременным явлением."
  };

  return (
    <div className="book-page">
      <div className="book-main">
        <div className="book-but">
          <BookCover image={bookData.coverImage} />
          <ActionButtons />
        </div>
        <div className="book-details">
          <BookInfo 
            title={bookData.title}
            author={bookData.author}
            year={bookData.year}
            publish={bookData.publish}
            isbn={bookData.isbn}
            pages={bookData.pages}
            description={bookData.description}
          />
          
        </div>
      </div>
    </div>
  );
}

export default BookPage;
