import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import api from '../../api'; // Ваш axios-клиент
import "./BookPage.css";

function BookPage() {
  const { id } = useParams(); // Получаем ID книги из URL
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Загружаем данные книги
  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await api.get(`/books/${id}`);
        setBook(response.data);
      } catch (err) {
        console.error("Ошибка при загрузке книги:", err);
        setError("Не удалось загрузить данные книги");
      } finally {
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]);

  // Показываем заглушки во время загрузки/ошибки
  if (loading) {
    return (
      <div className="book-page">
        <div className="loading">Загрузка книги...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="book-page">
        <div className="error">{error}</div>
      </div>
    );
  }

  if (!book) {
    return (
      <div className="book-page">
        <div className="not-found">Книга не найдена</div>
      </div>
    );
  }

  // Основной рендер
  return (
    <div className="book-page">
      <div className="book-main">
        <div className="book-but">
          <BookCover image={book.coverImage || '/images/default-book.png'} />
          <ActionButtons bookId={book.id} />
        </div>
        <div className="book-details">
          <BookInfo 
            title={book.title}
            author={book.author}
            year={book.year}
            publish={book.publish}
            isbn={book.isbn}
            pages={book.pages}
            description={book.description}
          />
        </div>
      </div>
    </div>
  );
}

export default BookPage;
