import axios from 'axios';

const api = axios.create({
  baseURL: 'https://ваш-бэкенд-адрес/api', // Замените на реальный URL
  timeout: 5000,
  headers: {
    'Content-Type': 'application/json',
    'ngrok-skip-browser-warning': 'true' // Если используете ngrok
  }
});


export default api;
