import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import BookCover from "../../components/BookCover/BookCover";
import BookInfo from "../../components/BookInfo/BookInfo";
import ActionButtons from "../../components/ActionButtons/ActionButtons";
import "./BookPage.css";
import axios from 'axios';

function BookPage() {
  const { bookId } = useParams(); // Получаем ID книги из URL
  const [bookData, setBookData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Конфигурация API
  const api = axios.create({
    baseURL: 'https://d527-178-208-64-74.ngrok-free.app',
    headers: {
      'ngrok-skip-browser-warning': 'true'
    },
    timeout: 5000
  });

  useEffect(() => {
    const fetchBookData = async () => {
      try {
        // Получаем все книги
        const response = await api.get('/books');
        const allBooks = response.data;
        
        // Находим нужную книгу по ID
        const selectedBook = allBooks.find(book => book.bookId.toString() === bookId);
        
        if (!selectedBook) {
          throw new Error("Книга не найдена");
        }

        setBookData({
          id: selectedBook.bookId,
          title: selectedBook.title,
          author: selectedBook.author,
          coverImage: selectedBook.coverUrl,
          year: selectedBook.publicationYear,
          publish: selectedBook.publisher,
          isbn: selectedBook.isbn,
          genre: selectedBook.genre,
          description: selectedBook.description,
          keywords: selectedBook.keywords
        });
      } catch (err) {
        console.error("Ошибка запроса:", err);
        setError(err.message || "Произошла ошибка при загрузке данных");
      } finally {
        setLoading(false);
      }
    };

    fetchBookData();
  }, [bookId]); // Зависимость от bookId - при изменении ID загружаем новую книгу

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!bookData) return <div className="error">Данные о книге не загружены</div>;

  return (
    <div className="book-page">
      <div className="book-main">
        <div className="book-but">
          <BookCover image={bookData.coverImage} />
          <ActionButtons bookId={bookData.id} />
        </div>
        <div className="book-details">
          <BookInfo {...bookData} />
        </div>
      </div>
    </div>
  );
}

export default BookPage;
